import { useEffect, useState } from "react"
import ShowUser from "../../EX9/components/ShowUsers"
import { getUserDetailsById } from "../../server/user.server"



const Users = () => {

    const [users, setUsers] = useState([])
    useEffect(async () => {
        const showUser = await getUserDetailsById()
        setUsers(showUser)


    })


    return (
        <div>
            {
                users.map(user => {
                    <ShowUser id={user.id} name={user.name}></ShowUser>
                })
            }
        </div>
    )

}

export default Users
