import {
    BrowserRouter,
    Routes,
    Route,
    Link
} from "react-router-dom";

import React, { useRef, useState } from 'react'
import Todo from "./Todo";

import { getUserTodosById } from "../../server/user.server";

function Main() {
    const userIdInputRef = useRef(null)
    const [userData, setuserData] = useState({})

    const handleSubmit = async (e) => {
        e.preventDefault()
        const userFetchedData = await getUserTodosById(userIdInputRef.current.value)
        console.log(userFetchedData)
        setuserData(userFetchedData)
    }
    return (
        <div>
            <input ref={userIdInputRef} placeholder='enter id' />
            <button onClick={handleSubmit}>Get User Details</button>
            <div>
                <p>{userData.name}</p>
                {userData.email}
            </div>
            {userData.name[0] === 'E' && <Todo userId={userData.id} />}
        </div>
    )
}
export default Main
