import { useState, useEffect } from "react"
import { getUserDetailsById } from "../../server/user.server"


function Todo({ userId }) {
    const [todos, setTodos] = useState([])

    useEffect(() => {
        const getTodos = async () => {
            try {
                const userTodo = await getUserDetailsById(userId)
                setTodos(userTodo)
            } catch (e) {
                console.log(e)
                setTodos([])

            }
        }
        getTodos()
    }, [userId])
    return (
        <ul>
            {
                todos.map(todo => {
                    return <li key={todo.id}>{todo.title}</li>
                }

                )
            }


        </ul>
    )
}
export default Todo
